<?php      class Live extends CI_Controller{          function index()          {              $this->load->model('Live_model');                $data['result'] = $this->Live_model->getData();              $data['page_title'] = "CI Hello World App!";                $this->load->view('Live_view',$data);          }		function xx()		{			$this->load->model('Live_model'); 							$t=$this->input->post('t');				$tx=$this->input->post('tx');			$this->helloworld_model->insert_TODO($t,$tx) ;            			$data['result'] = $this->helloworld_model->getData();              $data['page_title'] = "CI Hello World App!";  	            $this->load->view('helloworld_view',$data);  		}    }  ?>